<span class="line">        <span class="token class-name">ReedSolomon</span> reedSolomon <span class="token operator">=</span> <span class="token class-name">ReedSolomon</span><span class="token punctuation">.</span><span class="token function">create</span><span class="token punctuation">(</span><span class="token constant">DATA_SHARDS</span><span class="token punctuation">,</span> <span class="token constant">PARITY_SHARDS</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">        <span class="token keyword">int</span> shardSize <span class="token operator">=</span> <span class="token punctuation">(</span>data<span class="token punctuation">.</span>length <span class="token operator">+</span> <span class="token constant">DATA_SHARDS</span> <span class="token operator">-</span> <span class="token number">1</span><span class="token punctuation">)</span> <span class="token operator">/</span> <span class="token constant">DATA_SHARDS</span><span class="token punctuation">;</span></span>
<span class="line">        <span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">[</span><span class="token punctuation">]</span> shards <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token keyword">byte</span><span class="token punctuation">[</span><span class="token constant">TOTAL_SHARDS</span><span class="token punctuation">]</span><span class="token punctuation">[</span>shardSize<span class="token punctuation">]</span><span class="token punctuation">;</span></span>
