<span class="line">    <span class="token keyword">private</span> <span class="token keyword">static</span> <span class="token keyword">void</span> <span class="token function">uploadShardToNode</span><span class="token punctuation">(</span><span class="token class-name">Path</span> shardPath<span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
