<span class="line">        reedSolomon<span class="token punctuation">.</span><span class="token function">decodeMissing</span><span class="token punctuation">(</span>shards<span class="token punctuation">,</span> shardPresent<span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> shardSize<span class="token punctuation">)</span><span class="token punctuation">;</span></span>
